sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// Define getEmail function
	// Create a GlideRecrod for User table
	// Use sysparam_userID passed from the client side to retrieve a record from the User table
	getEmail: function() {
		var usrRecord = new GlideRecord("sys_user");
		usrRecord.get(this.getParameter('sysparm_userID'));
		return usrRecord.email + '';
	},
    type: 'GetEmailAddress'
});
